import config from "config";
import { EAS, SchemaEncoder } from "@ethereum-attestation-service/eas-sdk";
import {ethers} from "ethers";



const provider = new ethers.providers.EtherscanProvider(
    "sepolia",
    config.get('provider.apiKey')
);
const wallet = new ethers.Wallet(config.get('wallet.privateKey'), provider);
let total = 0;
let times = [];
for (let i = 0; i<2000; i++) {
    let start = performance.now()
    let signPromise = wallet.signMessage("")

    await signPromise.then((signature) => {
        let sig = ethers.utils.splitSignature(signature);
        //console.log(sig)
        let signingAddress = ethers.utils.verifyMessage("", sig);

        //console.log(signingAddress);
        var diff = performance.now() - start;
        if(diff>15) {
            console.log("sss")
        } else {
            times.push((diff).toFixed(10));
        }
    });
}

var json = JSON.stringify(times);
import * as fs from 'fs';
fs.writeFile('results_500char.json', json, 'utf8', () => console.log("done"));